mapper.select:
SELECT
IFNULL(x.user_name,'') as leader_name, t.oa_task_id, t.dept_id, t.task_name, t.status, t.important_level, t.difficult_level, t.start_date, t.end_date, t.is_team, t.source_type, t.sponsor_id,
  t.executor_id, t.appraiser_id, t.content, t.file, t.progress, t.expired_day, t.over_reason, t.create_time, t.detail
FROM
  oa_task t
LEFT JOIN oa_task_user u ON t.oa_task_id = u.task_id
LEFT JOIN sys_user su ON t.sponsor_id = su.user_id
LEFT JOIN
(
    SELECT GROUP_CONCAT(u.user_name) as user_name, u.dept_id FROM sys_dept d INNER JOIN sys_user u ON d.dept_id=u.dept_id INNER JOIN sys_user_role r ON u.user_id=r.user_id
    WHERE u.del_flag=0 AND d.del_flag=0 AND r.role_id=3 GROUP BY u.dept_id
) x ON t.dept_id=x.dept_id


    t.del_flag = 0
     AND u.user_id = #{userId}
     AND su.user_name like concat('%', #{userName}, '%')
    AND u.user_type = #{userType} 
    AND t.oa_task_id = #{oaTaskId} 
     and t.task_name like concat('%', #{taskName}, '%')



                AND t.status = 1 AND DATE_FORMAT(SYSDATE(), '%Y-%m-%d') <= DATE_FORMAT(t.end_date, '%Y-%m-%d')


                AND t.status = 1 AND DATE_FORMAT(SYSDATE(), '%Y-%m-%d') > DATE_FORMAT(t.end_date, '%Y-%m-%d')


                AND t.status = #{status}



     and t.important_level = #{importantLevel}
     and t.difficult_level = #{difficultLevel}

        AND t.create_time >= concat(#{cStartTime},' 00:00:01')


        and t.create_time <= concat(#{cEndTime},' 23:59:59')

     and t.is_team = #{isTeam}
     and t.dept_id = #{deptId}
     and t.source_type = #{sourceType}
     and t.sponsor_id = #{sponsorId}
     and t.executor_id = #{executorId}
     and t.appraiser_id = #{appraiserId}
     and t.content = #{content}
     and t.file = #{file}
     and t.expired_day = #{expiredDay}
     and t.progress = #{progress}

GROUP BY t.oa_task_id
ORDER BY t.create_time DESC, t.status ASC

mapper.update:
update oa_task
task_name = #{taskName}, dept_id = #{deptId}, status = #{status}, important_level = #{importantLevel}, difficult_level = #{difficultLevel}, start_date = #{startDate}, end_date = #{endDate}, is_team = #{isTeam}, source_type = #{sourceType}, sponsor_id = #{sponsorId}, content = #{content}, detail = #{detail}, file = #{file}, over_reason = #{overReason}, expired_day = #{expiredDay}, progress = #{progress}, create_time = #{createTime}, update_time = #{updateTime},
where oa_task_id = #{oaTaskId}
